import addToCart from './addToCart.js';
import cart from './cart.js';
import select from './select.js';
import slider from './slider.js';
import toggleMenu from './toggle_menu.js';
import thumbnail from './thumbnail.js';
import modal from './modal.js';

document.addEventListener('DOMContentLoaded', () => {
  slider('next', 'prev', '.slider-main__item', 'slider-main__item--active');
  slider(
    'next-modal',
    'prev-modal',
    '.slider-modal__item',
    'slider-modal__item--active'
  );
  toggleMenu();
  cart();
  select();
  addToCart();
  modal();
  thumbnail(
    'thumbnail-main',
    '.slider-main__item',
    'thumbnail-main__img',
    'slider-main__item--active',
    'thumbnail-main__item--active'
  );
  thumbnail(
    'thumbnail-modal',
    '.slider-modal__item',
    'thumbnail-modal__img',
    'slider-modal__item--active',
    'thumbnail-modal__item--active'
  );
});
